package ch03;

public class Ch0311Ex6Short {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int d = 2;
		int grd = 2;
		if(grd == 2 && (d=d+1) ==3 ) {
			System.out.println(d);
		}
		System.out.println(d);
		d = 2;
		grd = 2;
		if(grd == 2 || (d=d+1) ==3 ) {
			System.out.println(d);
		}
		System.out.println(d);

	}

}
